CREATE TABLE IF NOT EXISTS rules (`id` int NOT NULL PRIMARY KEY AUTO_INCREMENT, `lastchange` INT NOT NULL, `lastchangeby` VARCHAR(25) NOT NULL,`title` VARCHAR(255), `text` VARCHAR(1000), `admin` TINYINT(1) DEFAULT 0, `mod` TINYINT(1) DEFAULT 0, `guild` TINYINT(1) DEFAULT 0, `member` TINYINT(1) DEFAULT 0, `all` TINYINT(1) DEFAULT 0);
CREATE TABLE IF NOT EXISTS rules_signs (`player` VARCHAR(25) NOT NULL PRIMARY KEY, `signtime` INT);
